window.addEventListener("load",function(){document.querySelectorAll(".gform-settings-field__select_custom select").forEach(function(t){t.addEventListener("change",function(e){"gf_custom"===e.target.value&&(t.style.display="none",t.nextSibling.style.display="block")})}),document.querySelectorAll(".gform-settings-select-custom__reset").forEach(function(t){t.addEventListener("click",function(e){t.parentNode.style.display="none",t.parentNode.previousSibling.value="",t.parentNode.previousSibling.style.display="block"})})});